INSERT INTO shakeitapp_dcocktailgroups (dcg_name,dcg_name_translit,dcg_url,dcg_anno,dcg_order) VALUES
	 ('Безалкогольные','Non-alcoholic','non-alcoholic','Безалкогольные коктейли — это напитки, которые не содержат алкоголя, но при этом имеют приятный  вкус и могут освежить в жаркий день. Некоторые популярные безалкогольные коктейли включают фраппе, фраска, фраголино, мохито, фрапса, фрапе и фраппучино.',1),
	 ('Слабоалкогольные','Low-alcohol','low-alcohol','Слабоалкогольные коктейли содержат небольшое количество алкоголя, обычно от 1 до 7% объема. Они могут быть приготовлены из различных ингредиентов, таких как фрукты, ягоды, овощи, травы и специи. Некоторые популярные слабоалкогольные коктейли включают в себя мохито, маргариту, дайкири и май тай.',2),
	 ('Крепкие','Strong','strong','Крепкие алкогольные коктейли содержат большое количество алкоголя, обычно более 10% объема. Они часто изготавливаются с использованием крепких спиртных напитков, таких как виски, коньяк, текила или водка. Некоторые популярные крепкие алкогольные коктейли включают виски со льдом, водку с мартини и дамбуи с содовой.',3);
