INSERT INTO shakeitapp_dingredientsgroups (di_name,di_name_translit,di_url,di_anno,di_order) VALUES
	 ('Крепкий алкоголь','Strong alcohol','strong-alcohol','Крепкий алкоголь для коктейлей включает в себя различные виды спиртных напитков высокой крепости, таких как водка, виски, коньяк, текила, джин и другие. Они используются в качестве основных ингредиентов для приготовления коктейлей, добавляя им глубину вкуса, характер и структуру. Каждый из этих видов алкоголя имеет свои особенности и предпочтения в использовании, что позволяет создавать разнообразные и неповторимые коктейли.',1),
	 ('Настойки','Tinctures','tinctures','Настойки в коктейлях — это алкогольные напитки, используемые для придания вкуса и аромата коктейлям. Они могут быть изготовлены из различных ингредиентов и использоваться в различных коктейлях, включая шоты и лонг дринки. Некоторые из наиболее популярных настоек включают Ангостуру, Кампари, Куантро, Калуа и Егермейстер.',2),
	 ('Вермуты','Vermouth','vermouth','Вермут – это алкогольный напиток на основе вина с добавлением различных трав и специй. Он обычно имеет сладкий вкус и может быть белым, красным или розовым. Вермут используется во многих коктейлях, особенно в классических. Например, один из самых популярных коктейлей с вермутом – это Манхэттен. Для его приготовления нужно смешать виски, вермут и ангостуру. Еще один известный коктейль с вермутом – Мартини. Он делается из джина и вермута.',3),
	 ('Вина','Wines','wines','Коктейли на основе вина – это смешанные напитки, сделанные с использованием вина. Они могут быть приготовлены из различных типов вин – красного, белого, розового – и часто включают в себя добавление других ингредиентов, таких как фрукты, биттеры, вермуты, ликеры и гарниши.',4),
	 ('Пиво и сидр','Beer and cider','beer-and-cider','Коктейли с пивом и сидром обычно представляют собой смеси алкогольных напитков с добавлением фруктовых соков, спиртных ликеров или других ингредиентов для придания особого вкуса. Например, одним из популярных коктейлей с пивом является «Шансонье», который состоит из светлого пива, газировки и клюквенного сиропа. Коктейли с сидром обычно создаются на основе яблочного сидра с добавлением специй, фруктов или других ароматизаторов. Некоторые из них, такие как «Сайдерхаус Маргарита» или «Сидр-Лонг-Айленд», имеют яркий вкус и освежающий характер.',5),
	 ('Ликеры и биттеры','Liqueurs and bitters','liqueurs-and-bitters','Коктейли с ликерами – это смешанные алкогольные напитки, которые обычно содержат один или несколько видов ликеров. Ликеры – это сладкие, ароматные алкогольные напитки, изготовленные из фруктов, трав, специй, молока, сливок, яиц и других ингредиентов.',6),
	 ('Сиропы','Syrups','syrups','Сиропы являются важным ингредиентом во многих коктейлях. Они придают коктейлям сладкий вкус и могут добавить другие ароматы, такие как фруктовый, шоколадный или карамельный. Сиропы можно использовать в качестве основы для коктейлей, например, в безалкогольных коктейлях, или они могут быть добавлены в алкогольные коктейли для усиления вкуса. Некоторые из наиболее популярных сиропов, используемых в коктейлях, включают гренадин, блю кюрасао, трипл сек и амаретто.',7),
	 ('Соки','Juices','juices','Соки — важный компонент коктейлей. Они могут использоваться как самостоятельно, так и в сочетании с другими ингредиентами. Например, апельсиновый сок часто используется в качестве основного ингредиента в коктейле «Кровавая Мэри», а клюквенный сок — в коктейле «Космополитен». Также соки могут добавляться для придания коктейлю определенного вкуса или аромата. Например, ананасовый сок может использоваться для создания коктейля с тропическим вкусом, а лимонный сок — для создания кислого напитка.',8),
	 ('Вода и напитки','Water and drinks','water-and-drinks','Вода часто используется в коктейлях в качестве разбавителя, чтобы уменьшить крепость напитка. Она также может использоваться для приготовления коктейлей со льдом, таких как мохито или джин-тоник. В некоторых случаях вода может быть использована для создания основы для коктейля, как в случае с безалкогольными смузи или фраппе.',9),
	 ('Кофе и чай','Coffe and tea','coffe-and-tea','Чай и кофе также могут быть использованы в коктейлях для добавления аромата и вкуса. Например, холодный кофе часто используется в таких коктейлях, как фраппе или кофе по-ирландски, а чай может быть основой для таких коктейлей, как русский чай или английский завтрак.',10);
INSERT INTO shakeitapp_dingredientsgroups (di_name,di_name_translit,di_url,di_anno,di_order) VALUES
	 ('Фрукты и ягоды','Fruits and berries','fruits-and-berries','Фрукты и ягоды также используются в коктейлях как ингредиенты. Они могут быть свежими, замороженными или в виде сока. Некоторые популярные фрукты и ягоды, используемые в коктейлях, включают клубнику, малину, ананас, манго, киви и другие. Они могут быть использованы как отдельно, так и в комбинации с другими ингредиентами для создания различных вкусов и ароматов.',11),
	 ('Прочие ингредиенты','Other','other','Это может быть всё, что угодно. Например, в некоторых коктейлях используют кровь животных, насекомых или даже рыб. Также существуют коктейли с добавлением цветов, листьев, корней и стеблей растений. Некоторые коктейли могут содержать сырые яйца, молоко, сливки или мед. Есть даже коктейли, в которых используются драгоценные металлы, такие как золото или серебро. Но, конечно, такие коктейли могут быть не для всех приятными на вкус.',12);
